mapper.select:
select goods.no, code, goods.name as gname, manufacture, price, rate_sale as rateSale, category.name as cname, status.name as sname, c.iconStr, d.path as gImg from goods inner join category on goods.category_no = category.no inner join status on goods.status_no = status.no left join (select goods_no, group_concat(icon.name) as iconStr from goods_icon inner join icon on goods_icon.icon_no = icon.no group by goods_no) as c on goods.no = c.goods_no left join (select goods_no, path from goods_image group by goods_no) as d on goods.no = d.goods_no where 1=1
and status.no = ${status}
and category.no = ${category}
and exists(select 1 from icon as i where i.no = ${icon} and iconStr like concat('%', i.name, '%'))
and goods.name like '%${keyword}%'
and code like '%${keyword}%'
limit ${(page -1) * pageSize}, ${pageSize}

select count(goods.no) from goods inner join category on goods.category_no = category.no inner join status on goods.status_no = status.no left join (select goods_no, group_concat(icon.name) as iconStr from goods_icon inner join icon on goods_icon.icon_no = icon.no group by goods_no) as c on goods.no = c.goods_no where 1=1
and status.no = ${status}
and category.no = ${category}
and exists(select 1 from icon as i where i.no = ${icon} and iconStr like concat('%', i.name, '%'))
and goods.name like '%${keyword}%'
and code like '%${keyword}%'
